mapper.update:
update maintence set internet = #{internet}, tvfee = #{tvfee},
cleaning = #{cleaning}, waterfee = #{waterfee},
gasfee = #{gasfee}, electronic = #{electronic} 	
where property_no = #{property_no}

update picture set pic_division = #{pic_division}, 
pic_name = #{pic_name}, pic_savename = #{pic_savename}
where property_no = #{property_no}

mapper.select:
select pro.address
from
(select property_no, address from property
where
address like '%'||#{searchaddress}||'%'
and (deposit between #{deposit1} and #{deposit2})
and (month_fee between #{month_fee1} and #{month_fee2})
and isaccessible = 'true'
and issoldout = 'true'
 and property_type = #{property_type} 
 and rent_type = #{rent_type} 

   
       and (floor between 1 and 3) 
       and (floor between 4 and 6) 
       and (floor between 7 and 9) 
       and floor >= 10 
   

 and maintence_fee is null 
and built_year > 2014
) pro,
(select property_no from roomoption
where
total >= 0 
 and car = #{car} 
 and pet = #{pet} 
 and elevator = #{elevator} 
 and air_conditioner = #{air_conditioner} 
 and fridge = #{fridge} 
 and washing_machine = #{washing_machine} 
 and gas_stove = #{gas_stove} 
 and electric_stove = #{electric_stove} 
 and microwave = #{microwave} 
 and desk = #{desk} 
 and rack = #{rack} 
 and bed = #{bed} 
 and closet = #{closet} 
 and shoecabinet = #{shoecabinet} 
 and doorlock = #{doorlock} 
 and wifi = #{wifi} 
) op
where pro.property_no = op.property_no
